select.execute:
var nome = "maluf";
var q = y.query('select href, strong from html where '
                 + 'url="http://www.excelencias.org.br/@busca.php?nome='
                 + nome +'" and xpath=\'//div[@id="contem"]/a\'');
var results = q.results; 
var entries = <entries></entries>;

var id = null;
var fdp = null;

for each(a in results.a){
    id = new RegExp("id=(\\d+)").exec(a);
    id = id[1];
    fdp = a.strong;
    a.appendChild(<id>{id}</id>);
    a.appendChild(<nome>{fdp}</nome>);
    entries.appendChild(a);
}
response.object = results;
